/* 要求:
* (1)C0^2 + C1^2 + C2^2 + C3^2 = 1
* (2)C1 * C3 + C0 * C2 = 0
*/

/* 矩阵变换需要用到的参数 */
static Filter DBn = {
	.Len = 4,
	.C0 = (Element) 0.482962913144690,
	.C1 = (Element) 0.836516303737469,
	.C2 = (Element) 0.224143868041857,
	.C3 = (Element) -0.129409522550921,
};

static void sort(pElement input_Data, uint length)    //正排序
{
    uint i;
    uint count=0;
    Element transf[length];

    for(i=0; i<length; i=i+2)   //提取偶数列数据
    {
        transf[count++] = input_Data[i];
    }

    for(i=1; i<length; i=i+2)   //提取奇数列数据
    {
        transf[count++] = input_Data[i];
    }

    memset(input_Data, (Element) 0, length);

    for(i=0; i<length; i++)
    {
        input_Data[i] = transf[i];
    }
}

static void reverse_Sort(pElement input_Data, uint length)    //逆排序
{
    uint i;
    uint count=0;
    Element transf[length];

    for(i=0; i<length/2; i++)   //在偶数列放入数据
    {
        transf[count] = input_Data[i];
        count = count + 2;
    }

    count=1;
    for(i=length/2; i<length; i++)   //在奇数列放入数据
    {
        transf[count] = input_Data[i];
        count = count + 2;
    }

    memset(input_Data, (Element) 0, length);

    for(i=0; i<length; i++)
    {
        input_Data[i] = transf[i];
    }
}

/* 按分解矩阵进行分解 */
static void transform(pElement input_Data, pElement output_Data, uint length)
{
    uint i=0;
    uint count=0;

    for(i=0; i<=(length-3); i=i+2)
    {
        output_Data[count++] = DBn.C0 * input_Data[i] + DBn.C1 * input_Data[i+1] + DBn.C2 * input_Data[i+2] + DBn.C3 * input_Data[i+3];
        output_Data[count++] = DBn.C3 * input_Data[i] - DBn.C2 * input_Data[i+1] + DBn.C1 * input_Data[i+2] - DBn.C0 * input_Data[i+3];
    }
    output_Data[count++] = DBn.C2 * input_Data[0] + DBn.C3 * input_Data[1] + DBn.C0 * input_Data[length-2] + DBn.C1 * input_Data[length-1];
    output_Data[count] = DBn.C1 * input_Data[0] - DBn.C0 * input_Data[1] + DBn.C3 * input_Data[length-2] - DBn.C2 * input_Data[length-1];
}

/* 按重构矩阵进行重构 */
static void reverse_Transform(pElement input_Data, pElement output_Data, uint length)
{
    uint i=0;
    uint count=0;

    output_Data[count++] = DBn.C0 * input_Data[0] + DBn.C3 * input_Data[1] + DBn.C2 * input_Data[length-2] + DBn.C1 * input_Data[length-1];
    output_Data[count++] = DBn.C1 * input_Data[0] - DBn.C2 * input_Data[1] + DBn.C3 * input_Data[length-2] - DBn.C0 * input_Data[length-1];

    for(i=0; i<=(length-3); i=i+2)
    {
        output_Data[count++] = DBn.C2 * input_Data[i] + DBn.C1 * input_Data[i+1] + DBn.C0 * input_Data[i+2] + DBn.C3 * input_Data[i+3];
        output_Data[count++] = DBn.C3 * input_Data[i] - DBn.C0 * input_Data[i+1] + DBn.C1 * input_Data[i+2] - DBn.C2 * input_Data[i+3];
    }
}

/******************************************************************************
*	功能：利用矩阵进行分解重构
*	@srcData:	源信号;
*	@resData:	保存结果;
*	@srcLen:	源信号长度;
*******************************************************************************/
static void DWT_Matrix_Transform(pElement srcData, pElement resData, uint srcLen)
{
	uint i=0;
    Element out1Data[srcLen], out2Data[srcLen];

    memset(out1Data, 0, srcLen);
    memset(out2Data, 0, srcLen);

	printf("源信号：\n");
	for(i=0; i<srcLen; i++)
    {
        printf("%lf ", srcData[i]);
    }

    printf("\n\n");

    transform(srcData, out1Data, srcLen);
    // printf("DB4小波分解：\n");
    // for(i=0; i<srcLen; i++)
    // {
        // printf("%lf ", out1Data[i]);
    // }

    // printf("\n\n");

    sort(out1Data, srcLen);
    printf("DB4小波分解后正排序，前半部分表示近似信息，后半部分表示细节信息：：\n");
    for(i=0; i<srcLen; i++)
    {
        printf("%lf ", out1Data[i]);
    }

    printf("\n\n");

    reverse_Sort(out1Data, srcLen);
    // printf("DB4小波重构前逆排序：\n");
    // for(i=0; i<srcLen; i++)
    // {
        // printf("%lf ", out1Data[i]);
    // }

    // printf("\n\n");

    reverse_Transform(out1Data, out2Data, srcLen);
    printf("DB4小波重构：\n");
    for(i=0; i<srcLen; i++)
    {
        printf("%lf ", out2Data[i]);
    }

    printf("\n\n");

	return ;
}

/******************************************************************
*　功能：一维线性卷积函数
*　说明: 线性卷积,卷积结果的长度等于输入信号的长度 + 卷积核长度
*　输入参数: signal[],输入信号;
*			  filter[],卷积核;
*			  result[],卷积结果;
*             signalLen,输入信号长度;
*			  filterLen,卷积核长度.
*	返回值：返回卷积结果长度
******************************************************************/
static uint linear_Convlution(pElement signal, uint signalLen, pElement filter[DBNLEN], uint filterLen, pElement result)
{
	uint i=0, j=0, resultlen=0;

	resultlen = signalLen + filterLen - 1;

	for (i = 0; i < filterLen; i++)
    {
        for (j = 0; j <= i; j++)
            result[i] += signal[j] * (*filter[i - j]);
    }
    for (i = filterLen; i < signalLen; i++)
    {
        for (j = 0; j <filterLen; j++)
            result[i] += signal[i - j] * (*filter[j]);
    }
    for (i = signalLen; i < signalLen + filterLen - 1; i++)
    {
        for (j = i - signalLen + 1; j < filterLen; j++)
            result[i] += signal[i - j] * (*filter[j]);
    }
    return resultlen;
}


/***************************************************************************************
*	功能：信号进行延拓
*	输入参数:	@srcData : 	源信号
*				@srcLen : 	源信号长度
*				@extData : 	延拓后的信号
*				@extLen : 	延拓的长度
*				@method : 	METHOD_0:“zero”			零延拓
*			  				METHOD_1:"cycle"		周期延拓
*			  				METHOD_2:"symmetry"		对称延拓
*	返回值：无
***************************************************************************************/
static void signalExtend(pElement srcData, uint srcLen, pElement extData, uint extLen, uint method, uint filterLen)
{
	uint i=0, idx=0;

	switch(method)
	{
        case METHOD_0:
                    for(i=(filterLen-1); i>0; i--)  //前面延拓部分
                    {
                        extData[i-1] = 0;
                    }
                    for(i=filterLen-1, idx = 0; i<srcLen + (filterLen - 1); i++,idx++)  //中间部分是源信号
                    {
                        extData[i] = srcData[idx];
                    }
                    for(i=0; i<filterLen; i++)    //后面延拓部分
                    {
                        extData[i+srcLen+filterLen-1] = 0;
                    }
            break;

        case METHOD_1:
                    for(i=(filterLen-1); i>0; i--)  //前面延拓部分
                    {
                        extData[i-1] = srcData[(srcLen-1) - (filterLen-1) + i];
                    }
                    for(i=filterLen-1, idx = 0; i<srcLen + (filterLen - 1); i++,idx++)  //中间部分是源信号
                    {
                        extData[i] = srcData[idx];
                    }
                    for(i=0; i<filterLen; i++)    //后面延拓部分
                    {
                        extData[i+srcLen+filterLen-1] = srcData[i];
                    }
            break;

        default:
        case METHOD_2:
                    for(i=(filterLen-1), idx=0; i>0; i--, idx++)  //前面延拓部分
                    {
                        extData[i-1] = srcData[idx];
                    }
                    for(i=filterLen-1, idx = 0; i<srcLen + (filterLen - 1); i++,idx++)  //中间部分是源信号
                    {
                        extData[i] = srcData[idx];
                    }
                    for(i=0; i<filterLen; i++)    //后面延拓部分
                    {
                        extData[i+srcLen+filterLen-1] = srcData[(srcLen-1) - i];
                    }
            break;

	}
}

/***************************************************************************************
*	功能：一维小波一层分解(下采样)，提取奇数位置元素
*	输入参数:	@srcData : 		源信号
*				@resData : 		分解结果
*				@srcLen : 		源信号长度
*				@filter	:		滤波器系数矩阵
*				@filterLen : 	滤波器的长度
*				@ fpAppCoef:	存储近似系数的文件指针
*				@ fpDetCoef:	存储细节系数的文件指针
*	返回值：无
*	说明 ： 最终分解结果的长度为延拓后信号长度（srcLen+(filterLen-1)*2）与滤波器卷积长度（filterLen-1）之和，例如信号长度为8，滤波器长度为4，信号经延拓再与滤波器相卷，最终长度为8+(4-1)*2+(4-1)=17
***************************************************************************************/
static uint DWT_Convlution_Decompose(pElement srcData, uint srcLen,
									pElement resData, uint resLen,
									Element (*filter)[DBNLEN], uint filterLen,
									FILE* fpAppCoef, FILE* fpDetCoef)
{
	uint i=0, j=0;
	uint idx = 0;
	uint extLen = 0, convLen=0, tmpLen=0;
	pElement pFilter[filterLen];

    extLen = srcLen + (filterLen - 1) * 2;		//延拓后信号长度
    Element extData[extLen];		//延拓后信号
    memset(extData, (uchar) 0, sizeof(Element) * extLen);

    signalExtend(srcData, srcLen, extData, extLen, METHOD_2, filterLen);		//采用对称延拓方式延拓源信号
	// for(i=0; i<extLen; i++)
	// {
		// printf("%lf ", extData[i]);			//打印延拓后的信号
	// }
	// printf("\n\n");

/***************************************求解近似分量********************************************/
	convLen = extLen + filterLen - 1; 		 //卷积结果信号长度
    Element convData[convLen];
	memset(convData , 0, sizeof(Element) * convLen);

	for(i=0; i<filterLen; i++)
	{
		pFilter[i] = *(filter + 0) + i;
	}
	convLen = linear_Convlution(extData, extLen, pFilter, filterLen, convData);		//线性卷积结果
	// for(i=0; i<convLen; i++)
	// {
		// printf("%lf ", convData[i]);
	// }
	// printf("\n\n");

	tmpLen = convLen - (filterLen - 1) * 2;		//分解结果长度减去延拓部分
    Element tmpData[tmpLen];
	memset(tmpData , (uchar) 0, sizeof(Element) * tmpLen);

	for(i=(filterLen - 1), j = 0; i<(convLen - filterLen + 1); i++, j++)
	{
		tmpData[j] = convData[i];
		//printf("%lf ", tmpData[j]);
	}

	//printf("\n\n");

    for (i = 1, idx = 0; i < tmpLen; i += 2, idx++)			//去掉延拓部分，前后的长度为(filterLen - 1)
    {
		resData[idx] = tmpData[i];  					//下采样隔点采样，提取下标为奇数的元素，得到近似系数
		//printf("%lf ", resData[idx]);

		fprintf(fpAppCoef, "%lf,", resData[idx]);		//将数据流写入文件
    }
	//printf("\n\n");

	/**************************************求解细节分量*********************************************/
	convLen = extLen + filterLen - 1; 		 //卷积结果信号长度
	memset(convData , (uchar) 0, sizeof(Element) * convLen);

	for(i=0; i<filterLen; i++)
	{
		pFilter[i] = *(filter+1) + i;
	}
	printf("\n");
    convLen = linear_Convlution(extData, extLen, pFilter, filterLen, convData);

	tmpLen = convLen - (filterLen - 1) * 2;		//分解结果长度减去延拓部分
	memset(tmpData , (uchar) 0, sizeof(Element) * tmpLen);

	for(i=(filterLen - 1), j = 0; i<=(convLen - filterLen + 1); i++, j++)
	{
		tmpData[j] = convData[i];
		//printf("%lf ", tmpData[j]);
	}

	//printf("\n\n");
    for (i = 1; i < tmpLen; i += 2, idx++)				//去掉延拓部分，前后的长度为(filterLen - 1)
    {
		resData[idx] = tmpData[i];  					//下采样隔点采样，提取下标为奇数的元素，得到细节系数
		//printf("%lf ", resData[idx]);

		fprintf(fpDetCoef, "%lf,", resData[idx]);		//将数据流写入文件
    }
	//printf("\n\n");

	resLen = idx;

	return  resLen;
}

/***************************************************************************************
*	功能：上采样求卷积
*	输入参数:	@lohEle : 		近似系数或细节细节系数
*				@dataLen : 		输入数据长度,得到分解结果长度的一半
*				@convData : 	卷积结果
*				@filter	：		滤波器系数矩阵
*				@filterLen : 	滤波器的长度
*	返回值：无
***************************************************************************************/
static uint upSampConv(pElement lohEle, uint dataLen,
							pElement convData,
							pElement filter[DBNLEN], uint filterLen)
{
	uint i=0, idx=0;
	uint extLen = 0, convLen=0, sampLen=0;

	sampLen = dataLen * 2 + 1;				//从0下标开始,偶数位置插0后长度
	Element sampData[sampLen];  			//插0结果
	memset(sampData , 0, sizeof(Element) * sampLen);

	extLen = sampLen + (filterLen - 1) * 2;					//延拓后信号长度
	Element extData[extLen]; 								//延拓后信号
	memset(extData , 0, sizeof(Element) * extLen);

	for(i = 0; i<sampLen; i += 2)		    //上采样, 偶数位置插0
	{
		sampData[i] = 0;
		sampData[i+1] = lohEle[idx++];
	}
	// for(i=0; i<sampLen; i++)
	// {
		// printf("%lf ", sampData[i]);
	// }
	// printf("\n\n");

	signalExtend(sampData, sampLen, extData, extLen, METHOD_2, filterLen);		//采用对称延拓方式

	convLen = linear_Convlution(extData, extLen, filter, filterLen, convData);

	return convLen;
}

/***************************************************************************************
*	功能：一维小波重构(上采样)，重构近似系数和细节系数
*	输入参数:	@decData : 		源信号，已被分解的信号
*				@decLen : 		分解结果长度
*				@resData : 		分解信号经延拓并与滤波系数相卷的结果
*				@filter	：		滤波器系数矩阵
*				@filterLen : 	滤波器的长度
*	返回值：无
***************************************************************************************/
static uint RebuildCoef(pElement decData, uint decLen,
							pElement resData,
							Element (*filter)[DBNLEN], uint filterLen)
{
	uint i=0, j=0;
	uint ind=0;
	uint convLen_Low = 0, convLen_High = 0, convLen=0;
	pElement pFilter[filterLen];;

	ind = (uint) ((decLen) / 2);	//近似系数和细节系数各有一半

	Element lohEle[ind];
	memset(lohEle , 0, sizeof(Element) * ind);

	/*********************重构近似系数*********************/
	for(i = 0; i<ind; i++)		//前半部分表示近似信息
	{
		lohEle[i] = decData[i];
		//printf("%lf ", lohEle[i]);
	}
	//printf("\n\n");
	
	convLen_Low = ind * 2 + 1 + (filterLen - 1) * 2;	 	//插0，上采样，信号长度为ind * 2 + 1, 再经过卷积长度变为ind * 2 + 1 + (filterLen - 1) * 2

	Element convData_Low[convLen_Low];  			//近似系数卷积结果
	memset(convData_Low , (uchar) 0, sizeof(Element) * convLen_Low);

	for(i=0; i<filterLen; i++)
	{
		pFilter[i] = *(filter+2) + i;
	}
	convLen_Low = upSampConv(lohEle, ind, convData_Low, pFilter, filterLen);   	//插0，上采样

	/*******************重构细节系数*************************/

	convLen_High = ind * 2 + 1 + (filterLen - 1) * 2;

	Element convData_High[convLen_High];  			//细节系数卷积结果
	memset(convData_High, (uchar) 0, sizeof(Element) * convLen_High);

	memset(lohEle , 0, sizeof(Element) * ind);
	for(i = ind, j = 0; i < decLen; i++, j++)		//后半部分表示细节信息
	{
		lohEle[j] = decData[i];
		//printf("%lf ", lohEle[j]);
	}
	//printf("\n\n");

	for(i=0; i<filterLen; i++)
	{
		pFilter[i] = *(filter+3) + i;
	}
	convLen_High = upSampConv(lohEle, (decLen - ind), convData_High, pFilter, filterLen);

	/*********************合并结果**********************/
	convLen = convLen_High; 		//convLen_High == convLen_Low, 细节系数与近似系数长度相等
	for(i = 0; i < convLen; i++)
	{
		resData[i] = convData_Low[i] + convData_High[i];
	}

	return convLen;
}

/***************************************************************************************
*	功能：一维小波重构(上采样)，偶数位置插0
*	输入参数:	@decData : 		源信号，已被分解的信号
*				@decLen : 		分解结果长度
*				@resData : 		重构信号
*				@resLen	：		重构信号长度
*				@filter	：		滤波器系数矩阵
*				@filterLen : 	滤波器的长度
*	返回值：无
***************************************************************************************/
static void DWT_Convlution_Rebuild(pElement decData, uint decLen,
									pElement resData, uint resLen,
									Element (*filter)[DBNLEN], uint filterLen)
{
	uint i=0, j=0;

	uint convLen = decLen + 1 + (filterLen-1)*2 + (filterLen-1);
	Element convData[convLen];
	memset(convData, 0, sizeof(Element) * convLen);

	uint tmpLen = (convLen - (filterLen - 1));			//卷积结果长度减去(滤波器长度-1)
	Element tmpData[tmpLen];
	memset(tmpData, 0, sizeof(Element) * tmpLen);

	convLen = RebuildCoef(decData, decLen, convData, filter, filterLen);

	for(i = filterLen-1, j = 0; i < tmpLen; i++, j++)
	{
		tmpData[j] = convData[i];
	}

	resLen = (tmpLen - (filterLen - 1) * 2); 			//再减去减去延拓部分
	for(i = filterLen-1, j = 0; i < resLen ; i++, j++)
	{
		resData[j] = tmpData[i];
	}

	return ;
}

/***************************************************************************************
*	功能：数据缓冲区，类似于FIFO，建立需要一段时间
*	输入参数:	@inputData : 	输入信号
*				@Buffer : 		缓冲区
*				@Depth : 		缓冲区深度
*	返回值：无
***************************************************************************************/
static void Save_Input_Data(Element inputData, pElement Buffer, uint Depth)
{
	uint count=0;

	for(count = 0; count < (Depth - 1); count++)
	{
		*(Buffer + count) = *(Buffer + count + 1);
	}

	*(Buffer + Depth - 1) = inputData;
}







